import React, { useState } from 'react';

function App() {
  const [messages, setMessages] = useState([]);
  const [userMessage, setUserMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (userMessage.trim() === '') return;

    // Add the user's message to the chat window
    const newMessage = {
      text: userMessage,
      sender: 'user'
    };

    setMessages([...messages, newMessage]);

    // Clear the input box
    setUserMessage('');

    // Simulate a response from the RAG model
    setTimeout(() => {
      const ragResponse = {
        text: 'RAG Model gives appropriate response',
        sender: 'bot'
      };
      setMessages((prevMessages) => [...prevMessages, ragResponse]);
    }, 1000); // Simulate delay for the response
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <div className="flex-grow p-6 overflow-y-auto bg-white shadow-inner">
        <div className="space-y-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`p-3 rounded-lg shadow ${
                message.sender === 'user'
                  ? 'bg-blue-500 text-white self-end'
                  : 'bg-gray-300 text-gray-800 self-start'
              }`}
            >
              {message.text}
            </div>
          ))}
        </div>
      </div>

      {/* Input box */}
      <form onSubmit={handleSubmit} className="p-4 bg-gray-200">
        <div className="flex">
          <input
            type="text"
            value={userMessage}
            onChange={(e) => setUserMessage(e.target.value)}
            placeholder="Type your message..."
            className="flex-grow p-3 border border-gray-400 rounded-lg focus:outline-none"
          />
          <button
            type="submit"
            className="ml-4 px-6 py-3 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600"
          >
            Send
          </button>
        </div>
      </form>
    </div>
  );
}

export default App;
